#!/bin/bash

CONF_BASE64=""
TEMPLATE="CONF_TEMPLATE.txt"

PONTOS=2
PCGs=30

echo ${CONF_BASE64} | base64 --decode > ${TEMPLATE}

for ((i=0; i<=PONTOS; i++)); do
	
	for ((j=1; j<=PCGs; j++)); do

		if ((j < 10)); then
			sed "s|V|${i}|g" ${TEMPLATE} > PCG${i}-0${j}.txt
			sed -i "s|ZZ|0${j}|g" PCG${i}-0${j}.txt
			sed -i "s|X\.Y|${i}\.${j}|g" PCG${i}-0${j}.txt

		else
			sed "s|V|${i}|g" ${TEMPLATE} > PCG${i}-${j}.txt
			sed -i "s|ZZ|${j}|g" PCG${i}-${j}.txt
			sed -i "s|X\.Y|${i}\.${j}|g" PCG${i}-${j}.txt
		fi

		echo "Criado arquivo:" PCG${i}-${j}.txt

	done
done
